import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Button,
  FlatList,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import * as firebase from 'firebase/app';
import 'firebase/database';
// import response from '../data';
import { response, createJsonData } from '../FetchData';

function Articles() {
  response();
  var navigation = useNavigation();
  const renderItem = ({ item }) => <Item title={item.title} text={item.text} />;

  const Item = ({ title, text }) => (
    <View style={styles.item}>
      <Text>
        <Text style={styles.name}>{title}</Text>{' '}
        <Text style={styles.name}>{text}</Text>
      </Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.titleText}>Articles</Text>
      {/* <FlatList
        data={articlesArray}
        renderItem={renderItem}
        keyExtractor={(item) => item.title}
      /> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: { marginTop: 30, marginHorizontal: 20 },
  titleText: { fontSize: 35, textAlign: 'left' },
  signupText: { color: 'blue' },
});

export default Articles;
